import React, {useEffect, useState} from 'react';
import {Route, Switch} from 'react-router-dom';
import './App.css';

import BreweriesList from "./components/BreweriesList";
import DisplayBrewInfo from './components/DisplayBrewInfo';

const App= ()=> {

  const [data, setData]=useState([])

  const [brewDetail, setBrewDetail]= useState({})

  useEffect(()=>{
    fetch("https://api.openbrewerydb.org/breweries")
    .then(req =>req.json())
    .then(result => setData(result))
  },[])

  return (
    <div className="App">
      <Switch>
        <Route path='/breweries/:id' render={()=> <DisplayBrewInfo brew={brewDetail}/>}/>
        <Route path='/' render={()=> <BreweriesList data={data} setBrewDetail={setBrewDetail}/>}/>
      </Switch>
    </div>
  );
}

export default App;
