import React, {useEffect, useState} from 'react';
import {Route, Switch} from 'react-router-dom';
import './App.css';

import BreweriesList from "./components/BreweriesList";
import DisplayBrewInfo from './components/DisplayBrewInfo';
import Home from './components/Home';

const App= ()=> {

  const [data, setData]=useState([])

  const [brewDetail, setBrewDetail]= useState({})

  const [term, setTerm] = useState("")

  useEffect(()=>{
    console.log(term)
    fetch(`https://api.openbrewerydb.org/breweries?by_city=${term}`)
    .then(req =>{
      console.log(req.json())
      if(req.ok){
        return req.json()
      }else{
        throw Error(req.statusText)
      }  
    })
    .then(result => setData(result))
    .catch(err=>console.log(err))
  },[term])

  return (
    <div className="App">
              <Route path='/' render={()=> <Home setTerm={setTerm}/>}/>

      <Switch>
        <Route path='/breweries/:id' render={()=> <DisplayBrewInfo brew={brewDetail}/>}/>
        <Route path='/breweries' render={()=> <BreweriesList data={data} setBrewDetail={setBrewDetail}/>}/>
      </Switch>
    </div>
  );
}

export default App;
