import React from 'react';
import { withRouter } from 'react-router-dom';

const Home = ({term, setTerm, history}) =>{

    const handleOnChange = (e)=>{
        setTerm(e.target.value)
        history.push('/breweries')
    }

    return (
        <div>
            Enter a City: <input type="text" value={term} onChange={(e)=> handleOnChange(e)} />
            <input type="button" value="Search" onClick ={()=> setTerm(term)}/>
        </div>
    )
}

export default withRouter(Home);
