import React from 'react';
import { withRouter } from 'react-router-dom';

const Home = ({term, setTerm, history}) =>{

    const handleOnChange = ()=>{
        setTerm(term)
        history.push('/breweries')
    }

    return (
        <div>
            Enter a City: <input type="text" value={term} onChange={(e)=> setTerm(e.target.value)} />
            <input type="button" value="Search" onClick ={()=> handleOnChange()}/>
        </div>
    )
}

export default Home;
